package lux.pe.na.course;public class Book {    private String name;    private Integer copies;    public Book(String name, Integer copies) {        this.name = name;        this.copies = copies;    }    public Book() {    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public Integer getCopies() {        return copies;    }    public void setCopies(Integer copies) {        this.copies = copies;    }    @Override    public String toString() {        return "Book{" +                "name='" + name + '\'' +                '}';    }}